import React from 'react';
export default class ColorSelector extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: ['black', 'blue', 'green'],
    };
  }

  render() {
    return (
      <tr>
        {this.state.colors.map((color, index) => (
          <td key={index} style={{ backgroundColor: color }}>
            {color}
          </td>
        ))}
        <td></td>
      </tr>
    );
  }
}
