import React, { useState } from 'react';
const Box = ({ color }) => {
  const [newColor, setNewColor] = useState('black');
  const clickHandler = () => {
    setNewColor(color);
  };
  return (
    <>
      <td
        onClick={clickHandler}
        style={{
          backgroundColor: newColor,
          border: '1px solid gray',
          padding: '5px',
        }}
      >
        box
      </td>
    </>
  );
};

export default Box;
